mapper.select:
select 

from PRESENT.TF_B_DISCNT_DEPOSIT
where ACTION_EVENT_ID = #{actionEventId,jdbcType=DECIMAL}
  and PARTITION_ID = #{partitionId,jdbcType=DECIMAL}

mapper.delete:
delete from PRESENT.TF_B_DISCNT_DEPOSIT
where ACTION_EVENT_ID = #{actionEventId,jdbcType=DECIMAL}
  and PARTITION_ID = #{partitionId,jdbcType=DECIMAL}
